import { useEffect, useState } from 'react';
import './App.css';
import Designer from './Component/DesignerComponent/Designer';
import Sale from './Component/SaleComponent/Sale';
import Admin from './Component/Admin/Admin';
import Login from './Component/Login/Login';
import { Routes, Route,Redirect, Switch } from 'react-router-dom';
import Homepage from './Component/Homepage';
import Cookies from 'js-cookie';
import axios from "axios";



function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  
  useEffect(() => {
    // Kiểm tra xem người dùng đã đăng nhập chưa/ nếu đăng nhập thành công sẽ gán cookies, hoặc sẽ có role
    const role = Cookies.get('role');
    if(role){setIsAuthenticated(true)}
    
}, []);



  return (
    <Routes>
      <Route path="/login">
          {isAuthenticated ? (
            <Redirect to="/" /> // Điều hướng về trang chính nếu đã đăng nhập
            ) : (
              <Login />
            )}    
      </Route>
      <Route path="/" element={<Homepage />} />
    </Routes>
  )
}

export default App;
