import { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Sale from './SaleComponent/Sale';
import Designer from './DesignerComponent/Designer';
import Admin from './Admin/Admin';
import Header from './HeaderComponent/Header';

import axios from "axios";

function Homepage() {
    const[roleview, setRoleview] = useState()
    const[isadminview, SetIsadminview] = useState()
    const[issaleview, SetIssaleview] = useState()

    const[isdesignview, SetIsdesignview] = useState()
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate()

    const user = "user1"
    useEffect(() => {
        const fetchUserView = async () => {
            try {
                const response = await axios.get('http://localhost:3030/api/role/view', {withCredentials :true});
                setRoleview(response.data)
                setLoading(false)
            } catch (error) {

                navigate('/login')
            }
        };
        fetchUserView();
        
        
        
    }, [user]);
    if(roleview){
        roleview.map(element => {
            if(element==="view-admin"){SetIsadminview(true)}
            if(element==="view-sale"){SetIssaleview(true)}
 
        })
    }
    
    if (loading) {
        return <div>Loading...</div>;
      }
    return(
        <div>
            {isadminview ? <h1>Chào mừng bạn quay lại!</h1> : <h1>Xin chào, hãy đăng nhập!</h1>}
        </div>
    )
    
    
 
}

export default Homepage;
