import { useEffect, useState } from 'react';
import './App.css';
import Designer from './Component/DesignerComponent/Designer';
import Sale from './Component/SaleComponent/Sale';
import Admin from './Component/Admin/Admin';
import Login from './Component/Login/Login';
function App() {
  
  const[is_adminview,setIs_adminview] = useState(true)
  const[is_saleview,set_Issaleview] = useState(false)
  const[is_designerview,setIs_desingerview] = useState(false)
  const[user, setUser] = useState() 
  console.log(is_adminview)
  useEffect(() => {
    if(user.roledetailviews = "view-admin"){setIs_adminview(true)}
    if(user.roledetailviews = "view-sale"){set_Issaleview(true)}
    if(user.roledetailviews = "view-design"){setIs_desingerview(true)}
},[]);
  return (
    {if(is_adminview){<Admin />}},
    {if(is_saleview){<Sale user={user}/>}},
    {if(is_designerview){<Designer user={user}/>}},
    {if(user){<Login/>}}
    
  )
}

export default App;
