import { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Sale from './SaleComponent/Sale';
import Designer from './DesignerComponent/Designer';
import Admin from './Admin/Admin';
import Header from './HeaderComponent/Header';

import axios from "axios";

function Homepage() {
    const[view, setView] = useState()
    const[isadminview, SetIsadminview] = useState()
    const[issaleview, SetIssaleview] = useState()

    const[isdesignview, SetIsdesignview] = useState()

    const navigate = useNavigate()
    useEffect(() => {
        const fetchUserView = async () => {
            try {
                const response = await axios.get('http://localhost:3030/api/role/view', {withCredentials :true});
                setView(response.data)
            } catch (error) {

                navigate('/login')
            }
        };
        fetchUserView();
    }, []);
    if(view){
        if(view.length>1){
        view.forEach(element => {
            if(element==="view-admin"){
                SetIsadminview(true)
                return(<Designer/>)
            }
            if(element==="view-sale"){
                SetIssaleview(true)
                return(<Sale/>)
            }
            if(element==="view-design"){
                SetIsdesignview(true)
            }
        });
    }else{
        if(view==="view-admin"){
            SetIsadminview(true)
            return(<Designer/>)
        }
        if(view==="view-sale"){
            return(<Sale/>)
        }
        if(view==="view-design"){
            SetIsdesignview(true)
        }
    }

    }
    
    
 
}

export default Homepage;
